import styled from "@emotion/styled";

export const NotificationS = styled.h2`
  color: red;
  font-size: 25px;

  &:hover {
    background: black;
    color: white;
    -webkit-transform: scale(1.2);
    -ms-transform: scale(1.2);
    transform: scale(1.2);
    transform: scale(0.5);
  }
`;
