import { Component } from "react";
import { TitleFd, ListFd, ItemFd, BtnFd } from "./Feedback.styled";

export class Feedback extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  good = () => {
    this.setState((prevState) => {
      return {
        good: prevState.good + 1,
      };
    });
  };

  neutral = () => {
    this.setState((prevState) => {
      return {
        neutral: prevState.neutral + 1,
      };
    });
  };

  bad = () => {
    this.setState((prevState) => {
      return {
        bad: prevState.bad + 1,
      };
    });
  };

  countTotalFeedback = () => {};

  countPositiveFeedbackPernectagle = () => {};

  render() {
    const { good, neutral, bad } = this.state;
    return (
      <>
        <TitleFd>Please leave feedback</TitleFd>
        <ListFd>
          <ItemFd>
            <BtnFd onClick={good}>Good</BtnFd>
          </ItemFd>
          <ItemFd>
            <BtnFd onClick={neutral}>Neutral</BtnFd>
          </ItemFd>
          <ItemFd>
            <BtnFd onClick={bad}>Bad</BtnFd>
          </ItemFd>
        </ListFd>

        <h2>Statistics</h2>
        <ul>
          <li>Good: </li>
          <li>Neutral: </li>
          <li>Bad: </li>
          <li>Total: </li>
          <li>Positive fedback: </li>
        </ul>
      </>
    );
  }
}
