import { Component } from "react";
import { TitleFd, ListFd, ItemFd, BtnFd } from "./Feedback.styled";

export class Feedback extends Component {
  state = {
    good: 0,
    neutral: 0,
    bad: 0,
  };

  setGood = () => {
    this.setState((prevState) => {
      return {
        good: prevState.good + 1,
      };
    });
  };

  setNeutral = () => {
    this.setState((prevState) => {
      return {
        neutral: prevState.neutral + 1,
      };
    });
  };

  setBad = () => {
    this.setState((prevState) => {
      return {
        bad: prevState.bad + 1,
      };
    });
  };

  countTotalFeedback = () => {
    return this.state.good + this.state.neutral + this.state.bad;
  };

  countPositiveFeedbackPernectagle = () => {
    return (this.state.good / this.countTotalFeedback()) * 100;
  };

  render() {
    const { good, neutral, bad } = this.state;
    return (
      <>
        <TitleFd>Please leave feedback</TitleFd>
        <ListFd>
          <ItemFd>
            <BtnFd onClick={this.setGood}>Good</BtnFd>
          </ItemFd>
          <ItemFd>
            <BtnFd onClick={this.setNeutral}>Neutral</BtnFd>
          </ItemFd>
          <ItemFd>
            <BtnFd onClick={this.setBad}>Bad</BtnFd>
          </ItemFd>
        </ListFd>

        <h2>Statistics</h2>
        <ul>
          <li>Good: {this.state.good}</li>
          <li>Neutral: {this.state.neutral}</li>
          <li>Bad: {this.state.bad}</li>
          <li>Total: {this.countTotalFeedback()}</li>
          <li>Positive fedback: {this.countPositiveFeedbackPernectagle()}</li>
        </ul>
      </>
    );
  }
}
